import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Avatar from '@material-ui/core/Avatar';
import axios from 'axios';


const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});

export default function OportunityCard() {

    const [opportunity, setOpportunity] = useState([])
    const classes = useStyles();
    const bull = <span className={classes.bullet}>â€¢</span>;

    useEffect( () => {

        const request_dict = {
            and: [{"skill/role": {text: "machine learning", experience: "1-plus-year"}}],
        }

        axios.post("https://search.torre.co/opportunities/_search?currency=USD%24&page=0&periodicity=hourly&lang=es&size=20&aggregate=false&offset=0", request_dict)
        .then(res => {

            console.log(res);
            setOpportunity(res.data.results[0])

        })
    }, [])

    useEffect( () => {
        console.log(opportunity)
    }, [opportunity])

  return (
      <Grid container justify="center">
          <Grid item xs={8}>
              <Grid container justify="center">
                  <Grid item xs={12}>
                    <Card className={classes.root} variant="outlined">
                        <CardContent>
                            <Grid container>
                                <Grid item xs={3}>
                                    <Avatar alt={`${opportunity.organizations[0].name}`} src={`${opportunity.organizations[0].picture}`} />
                                </Grid>
                                <Typography className={classes.title} color="textSecondary" gutterBottom>
                                Word of the Day
                                </Typography>
                                <Typography variant="h5" component="h2">
                                be{bull}nev{bull}o{bull}lent
                                </Typography>
                                <Typography className={classes.pos} color="textSecondary">
                                adjective
                                </Typography>
                                <Typography variant="body2" component="p">
                                well meaning and kindly.
                                <br />
                                {'"a benevolent smile"'}
                                </Typography>
                            </Grid>
                        </CardContent>
                        <CardActions>
                            <Button size="small">Learn More</Button>
                        </CardActions>
                    </Card>
                  </Grid>
              </Grid>
          </Grid>
      </Grid>

  );
}
